import React from 'react';
import './Checkout.css';
import { useNavigate } from "react-router-dom";

export default function Checkout({ cart, removeFromCart, updateQuantity }) {
    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    const navigate = useNavigate();

    const handlePlaceOrder = () => {
        navigate('/checkout/payment');
    };

    const handleContinueShopping = () => {
        navigate('/store');
    };

    return (
        <div className="checkout-container">
            <div className="checkout-header-row">
                <h2>
                    <span>
                        <span className="your-highlight">Your <span className="cart-highlight">Cart</span></span>
                    </span>
                </h2>
                <span className="cart-items-count">{totalItems} Item{totalItems !== 1 ? 's' : ''}</span>
            </div>
            {/* <hr /> */}
            {cart.length === 0 ? (
                <div className="empty-cart-message">
                    <svg className="empty-cart-icon" xmlns="http://www.w3.org/2000/svg" width="64" height="64" fill="none" viewBox="0 0 24 24">
                        <path fill="#bbb" d="M7 20a2 2 0 1 0 0 4 2 2 0 0 0 0-4zm10 0a2 2 0 1 0 0 4 2 2 0 0 0 0-4zM7.16 16l.94-2h7.8a2 2 0 0 0 1.92-1.45l2.54-8.18A1 1 0 0 0 19.4 3H5.21l-.94-2H0v2h2l3.6 7.59-1.35 2.44A2 2 0 0 0 4 15a2 2 0 0 0 2 2h12v-2H6.42a.25.25 0 0 1-.24-.33z"/>
                    </svg>
                    <span className="empty-cart-text">
                        YOUR SHOPPING CART IS <b>EMPTY</b>
                    </span>
                </div>
            ) : (
                <>
                    <div className="cart-table">
                        <div className="cart-table-header">
                            <span className="col-product">Product Details</span>
                            <span className="col-price">Price</span>
                            <span className="col-qty">Quantity</span>
                            <span className="col-subtotal">Subtotal</span>
                        </div>
                        {cart.map(item => (
                            <div key={item.id} className="cart-table-row">
                                <div className="col-product">
                                    <img src={item.image} alt={item.name} />
                                    <div>
                                        <div className="product-name">{item.name}</div>
                                        <button className="remove-link" onClick={() => removeFromCart(item.id)}>Remove</button>
                                    </div>
                                </div>
                                <div className="col-price">${item.price.toFixed(2)}</div>
                                <div className="col-qty">
                                    <button
                                        className="qty-btn"
                                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                                        disabled={item.quantity <= 1}
                                    >&#60;</button>
                                    <span className="qty-value">{item.quantity}</span>
                                    <button
                                        className="qty-btn"
                                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                                    >&#62;</button>
                                </div>
                                <div className="col-subtotal">${(item.price * item.quantity).toFixed(2)}</div>
                            </div>
                        ))}
                    </div>
                    <div className="cart-footer-row">
                        <button className="continue-shopping" onClick={handleContinueShopping}>
                            ← Continue Shopping
                        </button>
                        <div className="cart-summary">
                            <h3>Total: ${total.toFixed(2)}</h3>
                            <button className="place-order-btn" onClick={handlePlaceOrder}>Place Order</button>
                        </div>
                    </div>
                </>
            )}
            {/* Always show Continue Shopping button at the bottom */}
            <div className="cart-footer-row">
                <button className="continue-shopping" onClick={handleContinueShopping}>
                    ← Continue Shopping
                </button>
            </div>
        </div>
    );
}
