import React, { useEffect, useState } from 'react';
import './Home.css';
import phones from '../data/phones';
import { Link, useNavigate } from 'react-router-dom';

export default function Home() {
    // Use first 3 phone images for the slider
    const sliderImages = phones.slice(0, 3).map(phone => phone.image);

    const [current, setCurrent] = useState(0);

    // Auto-slide every 3 seconds
    useEffect(() => {
        const interval = setInterval(() => {
            setCurrent(prev => (prev + 1) % sliderImages.length);
        }, 3000);
        return () => clearInterval(interval);
    }, [sliderImages.length]);

    // Dot click handler
    const goToSlide = idx => setCurrent(idx);

    // Interests data
    const interests = [
        {
            key: 'techy',
            title: 'Techy',
            desc: 'Latest features & innovation',
            img: '/images/interests/techy.jpg', // Place your images in public/images/interests/
            info: 'Explore phones packed with the newest technology, perfect for gadget lovers and early adopters.',
        },
        {
            key: 'professional',
            title: 'Professional',
            desc: 'Business & productivity',
            img: '/images/interests/professional.jpg',
            info: 'Find reliable phones with strong security and productivity features for your work life.',
        },
        {
            key: 'budget',
            title: 'Budget Friendly',
            desc: 'Affordable & reliable',
            img: '/images/interests/budget.jpg',
            info: 'Shop for quality phones that won’t break the bank, ideal for students and savvy shoppers.',
        },
        {
            key: 'camera',
            title: 'Camera Lovers',
            desc: 'Photography & video',
            img: '/images/interests/camera.jpg',
            info: 'Discover phones with top-notch cameras for capturing every moment in stunning detail.',
        },
    ];

    const navigate = useNavigate();

    const handleInterestClick = (interestKey) => {
        navigate(`/store?interest=${interestKey}`);
    };

    return (
        <>
            <div className="interests-section">
                <h2 className="interests-title">Jump into your interests</h2>
                <div className="interests-slider">
                    {interests.map(interest => (
                        <div
                            className="interest-card"
                            key={interest.key}
                            tabIndex={0}
                            onClick={() => handleInterestClick(interest.key)}
                            onKeyPress={e => {
                                if (e.key === 'Enter') handleInterestClick(interest.key);
                            }}
                        >
                            <div className="interest-img-wrapper">
                                <img src={interest.img} alt={interest.title} />
                                <div className="interest-tooltip">
                                    {interest.info}
                                </div>
                            </div>
                            <div className="interest-label">{interest.title}</div>
                            <div className="interest-desc">{interest.desc}</div>
                        </div>
                    ))}
                </div>
            </div>
            <div className="home-hero">
                <div className="home-hero-left">
                    <span className="home-badge">Unbeatable Deals</span>
                    <h1 className="home-title-main">
                        Discover Your Next<br />
                        Phone<br />
                        <span className="highlight">At Unbeatable Prices</span>
                    </h1>
                    <p className="home-desc">
                        Buy, sell, or exchange smartphones with confidence. We provide quality devices,<br />
                        trusted service, and the best prices in the market.
                    </p>
                    <ul className="home-features">
                        <li>✔ Certified pre-owned & new devices</li>
                        <li>✔ Fast & free delivery nationwide</li>
                        <li>✔ 7-day replacement guarantee</li>
                    </ul>
                    <Link to="/store" className="shop-now-btn">Shop Now</Link>
                </div>
                <div className="home-hero-right">
                    <div className="slider-image-container">
                        <img
                            src={sliderImages[current]}
                            alt="Phone or product"
                            className="slider-image"
                        />
                        <div className="slider-dots">
                            {sliderImages.map((_, idx) => (
                                <span
                                    key={idx}
                                    className={`dot${current === idx ? ' active' : ''}`}
                                    onClick={() => goToSlide(idx)}
                                    style={{ cursor: 'pointer' }}
                                ></span>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
