import React, { useState } from 'react';
import './FacetedSearch.css';
import Slider from '@mui/material/Slider';

export default function FacetedSearch({ addToCart, cart = [], saleIds = [], products = [] }) {
    const [search, setSearch] = useState('');
    const [brand, setBrand] = useState('');
    const [category, setCategory] = useState('');
    const [minPrice, setMinPrice] = useState(300);
    const [maxPrice, setMaxPrice] = useState(3000);
    const [year, setYear] = useState('');

    const uniqueBrands = [...new Set(products.map(p => p.brand))];
    const uniqueYears = [...new Set(products.map(p => p.year))].sort((a, b) => b - a);
    const uniqueCategories = [...new Set(products.map(p => p.category))];

    const filteredProducts = products.filter(product =>
        product.name.toLowerCase().includes(search.toLowerCase()) &&
        (brand === '' || product.brand === brand) &&
        (category === '' || product.category === category) &&
        (year === '' || product.year === Number(year)) &&
        product.price >= minPrice &&
        product.price <= maxPrice
    );

    return (
        <div className="faceted-container">
            <div className="filters">
                <h3>Filters</h3>
                <label>Search:</label>
                <input
                    type="text"
                    placeholder="Search products..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
                <label>Category:</label>
                <select value={category} onChange={(e) => setCategory(e.target.value)}>
                    <option value="">All Categories</option>
                    {uniqueCategories.map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <label>Brand:</label>
                <select value={brand} onChange={(e) => setBrand(e.target.value)}>
                    <option value="">All Brands</option>
                    {uniqueBrands.map(b => <option key={b} value={b}>{b}</option>)}
                </select>
                <label>Year:</label>
                <select value={year} onChange={(e) => setYear(e.target.value)}>
                    <option value="">All Years</option>
                    {uniqueYears.map(y => <option key={y} value={y}>{y}</option>)}
                </select>
                <label>Price Range:</label>
                <span>${minPrice} – ${maxPrice}</span>
                <Slider
                    value={[minPrice, maxPrice]}
                    onChange={(e, newValue) => {
                        setMinPrice(newValue[0]);
                        setMaxPrice(newValue[1]);
                    }}
                    min={Math.min(...products.map(p => p.price))}
                    max={Math.max(...products.map(p => p.price))}
                    step={50}
                    valueLabelDisplay="auto"
                />
            </div>

            <div className="product-grid">
                {filteredProducts.map(product => {
                    const alreadyAdded = cart.some(item => item.id === product.id);
                    const isOnSale = saleIds.includes(product.id);
                    return (
                        <div key={product.id} className="product-card">
                            <div className="image-wrapper">
                                <img src={product.image} alt={product.name} />
                            </div>
                            <h3>{product.name}</h3>
                            <p>{product.brand} — {product.year}</p>
                            <p>${product.price}</p>
                            <p className="category-label">{product.category}</p>
                            {isOnSale && (
                                <span className="sale-badge-inline">Sale 10% Off</span>
                            )}
                            {alreadyAdded ? (
                                <div className="item-added-msg">Item Added</div>
                            ) : (
                                <button className="add-cart-btn" onClick={() => addToCart(product)}>
                                    Add to Cart
                                </button>
                            )}
                        </div>
                    );
                })}
                {filteredProducts.length === 0 && <p>No products match your filters.</p>}
            </div>
        </div>
    );
}
